```cs
using System.Web.Mvc;

namespace NetFrameworkMvcWebsite.Controllers
{
    public class HomeController : Controller
    {
        public ActionResult Index()
        {
            ViewBag.QueryString =
                System.Web.HttpContext.Current.Request.QueryString;

            return View();
        }

    }
}
